import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

// ABI
import GreetingABI from "./artififacts/contracts/Greeting.sol/Greeting.json";

// Deployed Greeting Address
const greetingAddress = '0x5FbDB2315678afecb367f032d93F642f64180aa3';

function App() {

  const [message, setMessage] = useState("");

  // Helper Functions
  const requestAccount = async () => {
    await window.ethereum.request({ method: 'eth_requestAccounts' });
  }

  // Getting name from Greeting
  const fetchName = async () => {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(greetingAddress, GreetingABI.abi, provider);
      try {
        const age = contract.name();
        console.log(age);
      } catch (error) {
        console.log(error);
      }
    }
  }

  // Setting name of Greeting
  const setName = async () => {
    if (!message) return;

    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      const contract = new ethers.Contract(greetingAddress, GreetingABI.abi, signer);
      const transaction = await contract.setName(message);

      setMessage("");
      await transaction.wait();
      fetchName();
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Blockchain Data
        </p>

        <input
          onChange={(e) => setMessage(e.target.value)}
          value={message} type='text' name='value' id='value' /><br />
        <button onClick={fetchName}>Fetch</button><br />
        <button onClick={setName}>Set</button>
      </header>
    </div>
  );
}

export default App;
